mapper.update:
update clazz
name = #{name}, room = #{room}, begin_Date = #{beginDate}, end_date = #{endDate}, master_id = #{masterId}, subject = #{subject}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select c.*, e.name masterName
from clazz c left join emp e on c.master_id = e.id


        c.name like concat('%', #{name},'%')


        and c.begin_date >= #{begin}


        and c.end_date <= #{end}


order by c.update_time
